import React from 'react';
import Slider from 'react-slick';  // Import the slick carousel component
import { useNavigate } from 'react-router-dom';
import './EventDetail.css';  // Import the CSS file for styling

const EventDetail = ({ eventId }) => {
  // Event data for each event
  const events = {
    event1: {
      title: "KaSuTra-11",
      description: "Join KaSuTra-11, the most exciting event of the year! Get ready for engaging activities, networking, and surprises.",
      posters: ["/kasutra1.jpg", "/kasutra2.jpg", "/kasutra3.jpg"], // Images for carousel
      registrationLink: "https://example.com/register/ka-su-tra-11"
    },
    event2: {
      title: "PAB Sidang 24/25",
      description: "PAB Sidang 24/25 is a conference focused on academic excellence. Join us for insightful discussions and workshops.",
      posters: ["/pab1.jpg", "/pab2.jpg", "/pab3.jpg"],
      registrationLink: "https://example.com/register/pab-sidang"
    },
    // Add other events...
  };

  const event = events[eventId];

  const navigate = useNavigate();

  const handleRegistration = () => {
    // Redirect to the registration page
    window.location.href = event.registrationLink;
  };

  if (!event) {
    return <h2>Event Not Found</h2>;
  }

  // Settings for the slick carousel
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: true,
  };

  return (
    <div className="event-detail">
      <h1>{event.title}</h1>

      {/* Carousel for event posters */}
      <Slider {...carouselSettings} className="carousel-container">
        {event.posters.map((poster, index) => (
          <div key={index} className="carousel-slide">
            <img src={poster} alt={`${event.title} poster ${index + 1}`} className="event-poster" />
          </div>
        ))}
      </Slider>

      {/* Event description */}
      <div className="event-info">
        <p>{event.description}</p>
      </div>

      {/* Register button */}
      <button className="register-btn" onClick={handleRegistration}>
        Register Now
      </button>
    </div>
  );
};

export default EventDetail;
