import React from 'react';
import { useParams } from 'react-router-dom';
import './RegistrationPage.css';
import eventPoster from './PSSCMUSM.png'; // Replace with your event poster image path

const RegistrationPage = () => {
  const { eventId } = useParams(); // Get the eventId from the URL

  const handleSubmit = (e) => {
    e.preventDefault();
    const formData = new FormData(e.target);

    const registrationData = {
      eventId, // Include the eventId
      name: formData.get('name'),
      matricNumber: formData.get('matricNumber'),
      email: formData.get('email'),
      phoneNumber: formData.get('phoneNumber'),
    };

    fetch('http://localhost:5000/register', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(registrationData),
    })
      .then((response) => {
        if (response.ok) {
          alert('Registration successful!');
          e.target.reset();
        } else {
          alert('Error during registration!');
        }
      })
      .catch((error) => {
        console.error('Error:', error);
      });
  };

  return (
    <div className="registration-page">
      <div className="header">
        <img src={eventPoster} alt="Event Poster" className="event-poster" />
        <h1 className="event-title">Event ID: {eventId}</h1>
      </div>
      <form onSubmit={handleSubmit}>
        <label>NAME</label>
        <input type="text" name="name" placeholder="Enter Name" required />

        <label>MATRIC NUMBER</label>
        <input type="text" name="matricNumber" placeholder="Enter Matric Number" required />

        <label>EMAIL</label>
        <input type="email" name="email" placeholder="Enter Email" required />

        <label>PHONE NUMBER</label>
        <input type="text" name="phoneNumber" placeholder="Enter Phone Number" required />

        <button type="submit" className="register-btn">
          REGISTER NOW
        </button>
      </form>
    </div>
  );
};

export default RegistrationPage;
