import React from 'react';
import { NavLink } from 'react-router-dom';  // Import NavLink for active state

const Events = () => {
  const events = [
    { src: "/kasutra.jpg", title: "KaSuTra-11", page: "/event1" },
    { src: "/pab.jpg", title: "PAB Sidang 24/25", page: "/event2" },
    { src: "/kcl.jpg", title: "KCL 2024", page: "/event3" },
    { src: "/spm.jpg", title: "SPM 2.0", page: "/event4" },
  ];

  return (
    <section className="events-container">
      <h2>Featured Events</h2>
      <div className="events-row">
        {events.map((event, index) => (
          <NavLink to={event.page} key={index} className="event-card">
            <img src={event.src} alt={event.title} className="event-poster" />
            <p className="event-title">{event.title}</p>
          </NavLink>
        ))}
      </div>
    </section>
  );
};

export default Events;
