import React, { useRef } from 'react';
import Slider from 'react-slick';  // Import the slick carousel component
import { useNavigate } from 'react-router-dom';
import './EventDetail.css';  // Import the CSS file for styling

const EventDetail = ({ eventId }) => {
  const sliderRef = useRef(null);  // Create a reference to the Slick slider

  // Event data for each event
  const events = {
    event1: {
      title: "Karnival Sukan Tradisional kali ke-11",
      description: `Tak lekang dek panas, tak lapuk dek hujan. Tersentak sejenak mengimbau kembali zaman kanak-kanak, bermain permainan tradisional pada waktu petang.
      Kali ini Persatuan Seni Silat Cekak Malaysia Universiti Sains Malaysia bawakan kembali Karnival Sukan Tradisional Kali ke-11 yang menjadikan permainan tradisional sebagai sukan permainan.
      JADI JOM SERTAI BERAMAI-RAMAI DAN JANGAN LEPASKAN PELUANG KEEMASAN INI!!!`,
      registrationLink: "https://example.com/register/ka-su-tra-11"
    },
    // Add more events here if needed
  };

  const event = events[eventId];

  const handleRegistration = () => {
    // Redirect to the registration page
    window.location.href = event.registrationLink;
  };

  if (!event) {
    return <h2>Event Not Found</h2>;
  }

  // Carousel settings for Slick
  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 3000,
    arrows: false,  // Disable default arrows to use custom arrows
  };

  return (
    <div className="event-detail">
      <div className="carousel-container">
        <Slider ref={sliderRef} {...carouselSettings}>
          {/* Add your posters for the carousel */}
          <div className="carousel-slide">
            <img src="/kasutra/kasutra.jpg" alt="KaSuTra-11 Poster" />
          </div>
          <div className="carousel-slide">
            <img src="/kasutra/baju.jpg" alt="KaSuTra-11 Poster" />
          </div>
          <div className="carousel-slide">
            <img src="/kasutra/game.jpg" alt="KaSuTra-11 Poster" />
          </div>
          <div className="carousel-slide">
            <img src="/kasutra/logo.jpg" alt="KaSuTra-11 Poster" />
          </div>
        </Slider>

        {/* Custom Arrow Buttons */}
        <button
          className="carousel-arrow left-arrow"
          onClick={() => sliderRef.current.slickPrev()}  // Go to previous slide
        >
          &#8592;
        </button>
        <button
          className="carousel-arrow right-arrow"
          onClick={() => sliderRef.current.slickNext()}  // Go to next slide
        >
          &#8594;
        </button>
      </div>

      <div className="event-info">
        <h1>{event.title}</h1>
        <p>{event.description}</p>

        <p className="highlight">Kali ini Persatuan Seni Silat Cekak Malaysia Universiti Sains Malaysia bawakan kembali Karnival Sukan Tradisional Kali ke-11 yang menjadikan permainan tradisional sebagai sukan permainan.</p>

        <p className="highlight">Tempoh Pendaftaran: 29 Nov 2024 - 1 Jan 2024</p>

        <p className="highlight">Tempat: Stadium Minden, USM</p>

        <p className="highlight">Tarikh & Masa: 13 Januari 2024, 8.00 Pagi</p>

        <button className="register-btn" onClick={handleRegistration}>
          Register Now
        </button>
      </div>
    </div>
  );
};

export default EventDetail;
