  const App = () => {
    const [events, setEvents] = useState([
      { src: "/kasutra.jpg", title: "KaSuTra-11", page: "/event1" },
      { src: "/pab.jpg", title: "PAB Sidang 24/25", page: "/event2" },
      { src: "/kcl.jpg", title: "KCL 2024", page: "/event3" },
      { src: "/spm.jpg", title: "SPM 2.0", page: "/event4" },
    ]);

    const addEvent = (newEvent) => setEvents([...events, newEvent]);

  return (
    <Router> {/* Wraps the application in a Router for route management */}
      <Navbar /> {/* Navbar is displayed on all pages */}
      <main>
        <Routes> {/* Define routes for the app */}
          <Route path="/" element={<><InfoSection /><MainPoster /><EventButtons /><Events events={events} /></>} />
          <Route path="/events" element={<Events />} /> {/* Add route for events*/ }
          <Route path="/create-event" element={<CreateEvent addEvent={addEvent} />} />
          <Route path="/event1" element={<EventDetail eventId="event1" />} />
          <Route path="/event2" element={<EventDetail eventId="event2" />} />
          <Route path="/event3" element={<EventDetail eventId="event3" />} />
          <Route path="/event4" element={<EventDetail eventId="event4" />} />
          <Route path="/register/:eventId" element={<RegistrationPage />} /> {/* Update Registration to RegistrationPage*/ }
          <Route path="/login" element={<Login />} /> {/* Add Login route */}
          <Route path="/register" element={<Register />} /> {/* Add Register route */}
        </Routes>
      </main>
      <Footer /> {/* Footer is displayed on all pages */}
    </Router>
  );
};

export default App;
