// MainPoster.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';

const MainPoster = () => {
  const posters = [
    "/kasutra.jpg", 
    "/pab.jpg", 
    "/spm.jpg",  
    "/kcl.jpg"
  ];
  
  const [currentPoster, setCurrentPoster] = useState(0);
  const navigate = useNavigate(); // Hook to navigate to different routes

  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentPoster((prev) => (prev + 1) % posters.length);
    }, 3000); // Automatically cycle through posters every 3 seconds

    return () => clearInterval(interval); // Cleanup interval on unmount
  }, [posters.length]);

  const handlePosterClick = () => {
    // Dynamically navigate to the event detail page based on the current poster
    const eventId = `event${currentPoster + 1}`;
    navigate(`/event/${eventId}`); // Navigate to the event details page
  };

  return (
    <section className="main-poster">
      <a href="#" onClick={handlePosterClick}>
        <img src={posters[currentPoster]} alt="Main Event Poster" className="rotating-poster" />
      </a>
    </section>
  );
};

export default MainPoster;
