import React from 'react';
import { useParams } from 'react-router-dom';

const EventDetail = () => {
  // Get the event ID from the URL
  const { eventId } = useParams();

  // Sample event data (you can modify this as per your actual data)
  const eventDetails = {
    event1: {
      title: 'KaSuTra-11',
      description: 'KaSuTra-11 is an exciting event for all tech enthusiasts. This year, we have an array of activities lined up...',
      image: '/kasutra.jpg',
    },
    event2: {
      title: 'PAB Sidang 24/25',
      description: 'PAB Sidang 24/25 offers various conferences and seminars. Learn from industry leaders and experts...',
      image: '/pab.jpg',
    },
    event3: {
      title: 'KCL 2024',
      description: 'KCL 2024 is an innovative event for future leaders. Engage with mentors, participate in workshops...',
      image: '/kcl.jpg',
    },
    event4: {
      title: 'SPM 2.0',
      description: 'SPM 2.0 brings a new wave of educational events, including workshops and tutoring...',
      image: '/spm.jpg',
    },
    event5: {
      title: 'KWD 10',
      description: 'KWD 10 is the 10th anniversary event, packed with performances, exhibitions, and much more...',
      image: '/kwd10.jpg',
    },
  };

  // Fetch event data based on the eventId
  const event = eventDetails[eventId];

  return (
    <section className="event-detail">
      <div className="event-header">
        <img src={event.image} alt={event.title} className="event-image" />
        <div className="event-info">
          <h1 className="event-title">{event.title}</h1>
          <p className="event-description">{event.description}</p>
        </div>
      </div>

      {/* Registration Section */}
      <div className="registration-form">
        <h3>Register for the Event</h3>
        <form>
          <div className="form-group">
            <label htmlFor="name">Full Name</label>
            <input type="text" id="name" name="name" required />
          </div>
          <div className="form-group">
            <label htmlFor="email">Email</label>
            <input type="email" id="email" name="email" required />
          </div>
          <button type="submit">Register</button>
        </form>
      </div>
    </section>
  );
};

export default EventDetail;
