import React from 'react';
import { useParams } from 'react-router-dom';

const EventDetail = () => {
  const { eventId } = useParams();

  // Sample data for events (replace this with actual event data)
  const eventDetails = {
    event1: {
      title: 'KaSuTra-11',
      description: 'This is a description for KaSuTra-11 event.',
      image: '/kasutra.jpg',
    },
    event2: {
      title: 'PAB Sidang 24/25',
      description: 'This is a description for PAB Sidang 24/25 event.',
      image: '/pab.jpg',
    },
    event3: {
      title: 'KCL 2024',
      description: 'This is a description for KCL 2024 event.',
      image: '/kcl.jpg',
    },
    event4: {
      title: 'SPM 2.0',
      description: 'This is a description for SPM 2.0 event.',
      image: '/spm.jpg',
    },
    event5: {
      title: 'KWD 10',
      description: 'This is a description for KWD 10 event.',
      image: '/kwd10.jpg',
    },
  };

  const event = eventDetails[eventId]; // Fetch the event based on URL param

  if (!event) {
    return <div>Event not found!</div>;
  }

  return (
    <section className="event-detail">
      <div className="event-header">
        <img src={event.image} alt={event.title} className="event-image" />
        <div className="event-info">
          <h1 className="event-title">{event.title}</h1>
          <p className="event-description">{event.description}</p>
        </div>
      </div>

      {/* Registration Form */}
      <div className="registration-form">
        <h3>Register for the Event</h3>
        <form>
          <label htmlFor="name">Full Name</label>
          <input type="text" id="name" name="name" required />

          <label htmlFor="email">Email</label>
          <input type="email" id="email" name="email" required />

          <button type="submit">Register</button>
        </form>
      </div>
    </section>
  );
};

export default EventDetail;
