import React from 'react';
import { useNavigate } from 'react-router-dom';

const EventDetail = ({ eventId }) => {
  // Event data for each event
  const events = {
    event1: {
      title: "KaSuTra-11",
      description: "Join KaSuTra-11, the most exciting event of the year! Get ready for engaging activities and networking.",
      image: "/kasutra.jpg",
      registrationLink: "https://example.com/register/ka-su-tra-11"
    },
    event2: {
      title: "PAB Sidang 24/25",
      description: "PAB Sidang 24/25 is a conference focused on academic excellence. Join us for insightful discussions.",
      image: "/pab.jpg",
      registrationLink: "https://example.com/register/pab-sidang"
    },
    event3: {
      title: "KCL 2024",
      description: "KCL 2024 is the year to advance your knowledge and career. Join us for workshops and lectures.",
      image: "/kcl.jpg",
      registrationLink: "https://example.com/register/kcl-2024"
    },
    event4: {
      title: "SPM 2.0",
      description: "SPM 2.0 is a revision program that helps students excel in their exams. Don't miss it!",
      image: "/spm.jpg",
      registrationLink: "https://example.com/register/spm-2-0"
    },
    event5: {
      title: "KWD 10",
      description: "Celebrate the 10th anniversary of KWD with us. A grand event for all ages.",
      image: "/kwd10.jpg",
      registrationLink: "https://example.com/register/kwd-10"
    },
  };

  const event = events[eventId];

  const navigate = useNavigate();

  const handleRegistration = () => {
    // Redirect to the registration page
    window.location.href = event.registrationLink;
  };

  if (!event) {
    return <h2>Event Not Found</h2>;
  }

  return (
    <div className="event-detail">
      <h1>{event.title}</h1>
      <img src={event.image} alt={event.title} />
      <p>{event.description}</p>
      <button onClick={handleRegistration}>Register Now</button>
    </div>
  );
};

export default EventDetail;
